graphql.query:
query getCustomFields($siteId: ID!){
    site(id: $siteId) {
        customFields {
            name
            type
            key
            props {
                currencyType
                options
                linkTag
                minNumericValue
                maxNumericValue
            }
        }
    }
}

mutation customFieldCreation ($siteId: ID!, $field: CustomFieldInput!){
    site(id: $siteId) {
        createCustomField(field: $field) {
        name
        type
        key
        props {
            currencyType
            options
            linkTag
            minNumericValue
            maxNumericValue
            }
        }
    }
}

mutation deleteCustomField ($siteId: ID!, $key: ID!){
    site(id: $siteId) {
        deleteCustomField(key: $key)
    }
}

