graphql.query:
query assetResources($siteID: ID!, $pagination: AssetsPaginationInputValidated){
  site(id: $siteID){
    assetResources(
      assetPagination: $pagination,
      fields: [ 
        "assetBasicInfo.name",
        "assetBasicInfo.type",
        "url"
      ],
      filters: {
        conjunction: AND,
        conditions: [
          {
            operator: LIKE,
            path: "assetBasicInfo.type",
            value: "Printer"
          }
        ]
      }
    ) {
      total
      pagination {
        limit
        current
        next
        page 
      }
      items
    }
  }
}

