graphql.query:
mutation {
  appAccount{
    addWebhook(webhookParams: {
      name: "Name",
      description: "Description",
      payloadUrl: "https://www.site.com",
      resourcesFields: ["antiviruses","assetBasicInfo","computerSystems"],
      masterdata: ["assetTypes"],
      enabled: true,
      eventsSubscribed: ["INSERT","DELETE","UNLINK"],
      version: 2
    }) {
      description
      name
    }
  }
}

mutation {
  appAccount{
    addWebhook(webhookParams: {
      name: "Name",
      description: "Description",
      payloadUrl: "https://www.site.com",
      resourcesFields: ["antiviruses","assetBasicInfo","computerSystems"],
      enabled: true,
      eventsSubscribed: ["INSERT","DELETE","UNLINK"],
      version: 2
    }) {
      description
      name
    }
  }
}

mutation createWebhook($params: AddWebhookSecuredInput){
  appAccount{
    addWebhook(webhookParams: $params) {
      description
      name
    }
  }
}
